import React, { useEffect } from "react";
import "./Favorites.css";
const favLocations = []; //List of Objects of Locations added to favorite;

export function addToFavorites(
  locationKey,
  locationName,
  locationMetrics,
  weatherDescription
) {
  favLocations.push([
    locationKey,
    locationName,
    locationMetrics,
    weatherDescription,
  ]);
}
export const Favorites = ({ newKey }) => {
  const [location, setFavLocations] = React.useState([favLocations]);

  useEffect(() => {
    setFavLocations((prevState) => {
      favLocations: [...prevState, favLocations];
    });
  }, [favLocations]);
  return (
    <div className="fav-container">
      <div className="fav-wrapper">
        {favLocations.length > 0 ? (
          <ul className="fav-items">
            {favLocations.map((local) => {
              <a>
                <li>
                  <h3>{local[0]}</h3>
                  <h5>{local[1]}</h5>
                  <h6>{local[2]}</h6>
                </li>
              </a>;
            })}
          </ul>
        ) : (
          <h1 className="fav-empty">No Favorite Locations.</h1>
        )}
      </div>
    </div>
  );
};
