import React from "react";
import App from "../App";
import "./NavBar.css";
const NavBar = () => {
  const [activeBar, setActiveBar] = React.useState(0);

  const changeActiveBar = () => {
    if (activeBar == 0) {
      setActiveBar(1);
      <App currPage={activeBar} />;
    }
    if (activeBar == 1) {
      setActiveBar(0);
      <App currPage={activeBar} />;
    }
  };
  return (
    <div className="topnav" id="myTopnav">
      <h1 className="navTitle">Herolo Weather App</h1>
      <a
        href="#"
        className={activeBar == 1 ? "active" : ""}
        onClick={changeActiveBar}
      >
        Favorites
      </a>
      <a
        href="#"
        className={activeBar == 0 ? "active" : ""}
        onClick={changeActiveBar}
      >
        Home
      </a>
    </div>
  );
};
export default NavBar;
