import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlassArrowRight } from "@fortawesome/free-solid-svg-icons";
import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import WeatherCard from "./WeatherCard";
import "./Weather.css";
import {
  searchInput,
  currWeather,
  foreCast,
} from "../WeatherAPI/WeatherConfig";
const Weather = () => {
  const [autoCompleteData, setAutoCompleteData] = React.useState([]);
  const [search, setSearch] = React.useState("Tel Aviv");
  const [weatherData, setData] = React.useState(null);
  const makeAPICall = async (e) => {
    try {
      setSearch(e.target.value);
      const response = await fetch(searchInput(e.target.value), {
        mode: "cors",
      });
      const data = await response.json();
      setData(data);
      var locationsArray = [];
      data.map((a) => locationsArray.push(a.LocalizedName));
      setAutoCompleteData(locationsArray);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="weather-container">
      <div className="searchBar">
        <button id="searchIcon">
          <FontAwesomeIcon icon={faMagnifyingGlassArrowRight} />
        </button>
        <Autocomplete
          freeSolo
          id="searchInput"
          disableClearable
          onKeyDown={makeAPICall}
          options={autoCompleteData.map((option) => option)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Location.."
              InputProps={{
                ...params.InputProps,
                type: "search",
              }}
            />
          )}
        />
      </div>
      <WeatherCard location={search} />
    </div>
  );
};
export default Weather;
