import React, { useEffect } from "react";
import "./WeatherCard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart, faSun, faCloud } from "@fortawesome/free-solid-svg-icons";
import { currWeather, foreCast } from "../WeatherAPI/WeatherConfig";
import Loader from "../Loader/Loader";
import { addToFavorites, updater } from "../Favorites/Favorites";
const WeatherCard = ({ location }) => {
  //States
  const [weatherMetric, setMetric] = React.useState(null);
  const [locationName, setLocationName] = React.useState("Tel Aviv");
  const [weatherText, setWeatherText] = React.useState(null);
  const [cardData, setCard] = React.useState([]);
  const [loader, setLoader] = React.useState(true);
  const [local, setLocal] = React.useState("215854");

  //Fetch Current Weather
  const getCurrentWeather = async () => {
    try {
      const response = await fetch(currWeather(local), {
        mode: "cors",
      });
      const foreCastresponse = await fetch(foreCast(local, true), {
        mode: "cors",
      });
      const data = await response.json();
      const foreCastdata = await foreCastresponse.json();
      setMetric(
        data[0].Temperature.Metric.Value +
          "\xB0" +
          data[0].Temperature.Metric.Unit
      );
      setWeatherText(data[0].WeatherText);
      setCard(foreCastdata.DailyForecasts);
      if (cardData && data) {
        setLoader(false);
        if (location) {
          setLocationName(location.LocalizedName);
        }
      }
    } catch (err) {
      console.log(err);
    }
  };

  //Add To Favorites Function.
  const favoriteHandler = () => {
    addToFavorites(local, locationName, weatherMetric, weatherText);
    updater();
  };

  //Update Page if Location is Changed.
  useEffect(() => {
    if (location) {
      if (location.LocalizedName != "Tel Aviv") {
        setLocal(location.Key);
      }
    }
    getCurrentWeather();
  }, [local]);

  return (
    <div className="wrapper">
      <div className="content">
        <div className="weather-card">
          <div className="favorites-container">
            <button className="favBtn">
              <span>Favorite</span>
            </button>
          </div>
          <div className="quick-info">
            {locationName}
            <span className="sun"></span>
            <h3 id="cityName"></h3>
            <span className="sunx"></span>

            <h6 id="currTemp">{weatherMetric}</h6>
          </div>
        </div>
        <div className="weather-description">
          <h1 id="weather-desc">{weatherText}</h1>
        </div>
        <div className="day-cards">
          {loader ? (
            <Loader />
          ) : (
            <ul className="cards">
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[0].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                <h6>
                  {cardData[0].Temperature.Maximum.Value +
                    "\xB0" +
                    cardData[0].Temperature.Maximum.Unit}
                </h6>
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[1].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                <h6>
                  {cardData[1].Temperature.Maximum.Value +
                    "\xB0" +
                    cardData[1].Temperature.Maximum.Unit}
                </h6>
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[2].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                <h6>
                  {cardData[2].Temperature.Maximum.Value +
                    "\xB0" +
                    cardData[2].Temperature.Maximum.Unit}
                </h6>
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[3].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                <h6>
                  {cardData[3].Temperature.Maximum.Value +
                    "\xB0" +
                    cardData[3].Temperature.Maximum.Unit}
                </h6>
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[4].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                <h6>
                  {cardData[4].Temperature.Maximum.Value +
                    "\xB0" +
                    cardData[4].Temperature.Maximum.Unit}
                </h6>
              </li>
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};
export default WeatherCard;
