import React, { useEffect } from "react";
import "./Favorites.css";
const LocationsList = []; //List of Objects of Locations added to favorite;

export function addToFavorites(
  Key,
  locationName,
  locationMetrics,
  weatherDescription
) {
  const newLocation = {
    locationKey: Key,
    name: locationName,
    temperature: locationMetrics,
    weatherDesc: weatherDescription,
  };
  LocationsList.push(newLocation);
}
export const Favorites = () => {
  console.log(LocationsList);

  return (
    <div className="fav-container">
      <div className="fav-wrapper">
        {LocationsList.length > 0 ? (
          <ul className="fav-items">
            {LocationsList.map((local) => {
              console.log(local);
              <a>
                <li>
                  <h3>{local[0]}</h3>
                  <h5>{local[1]}</h5>
                  <h6>{local[2]}</h6>
                </li>
              </a>;
            })}
          </ul>
        ) : (
          <h1 className="fav-empty">No Favorite Locations.</h1>
        )}
      </div>
    </div>
  );
};
