import React from "react";
import "./WeatherCard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { currWeather, foreCast } from "../WeatherAPI/WeatherConfig";
const WeatherCard = (location) => {
  const getCurrentWeather = async (e) => {
    try {
      const response = await fetch(currWeather(location.Key), {
        mode: "cors",
      });
      const data = await response.json();
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="wrapper">
      <div className="content">
        <div className="weather-card">
          <div className="favorites-container">
            <i id="favIcon">
              <FontAwesomeIcon icon={faHeart} />
            </i>
            <button>Add To Favorite</button>
          </div>
          <div className="quick-info">
            <img src=""></img>
            <h3 id="cityName">{location.LocalizedName}</h3>
            <h6 id="currTemp">
              {location.Temperature.Value + location.Temperature.Unit}
            </h6>
          </div>
        </div>
        <div className="weather-description">
          <h1 id="weather-desc">{location.WeatherText}</h1>
        </div>
        <div className="day-cards">
          <ul>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};
export default WeatherCard;
