  };
  return (
    <div className="fav-container">
      <h1 id="favTitle">Favorites</h1>
      <div className="fav-wrapper">
        {LocationsList.length > 0 ? (
          <ul className="fav-items">
            {pState.map((local) => {
              return (
                <li key={local.locationKey}>
                  <button
                    id="list-click"
                    onClick={clickHandler}
                    item-key={local.locationKey}
                  >
                    <h3 id="city-name">{local.name}</h3>
                    <h6 id="city-temp">{local.temperature}</h6>
                    <h4 id="city-desc">{local.weatherDesc}</h4>
                  </button>
                </li>
              );
            })}
          </ul>
        ) : (
          <h1 className="fav-empty">No Favorite Locations.</h1>
        )}
      </div>
    </div>
  );
};
