import React, { useEffect } from "react";
import "./Favorites.css";
const LocationsList = []; //List of Objects of Locations added to favorite;
var updated = 0;
export function addToFavorites(
  Key,
  locationName,
  locationMetrics,
  weatherDescription
) {
  const newLocation = {
    locationKey: Key,
    name: locationName,
    temperature: locationMetrics,
    weatherDesc: weatherDescription,
  };
  LocationsList.push(...LocationsList, newLocation);
  updated += 1;
}

export const Favorites = () => {
  const [pState, setNewState] = React.useState(LocationsList);
  const [upd, setUpdated] = React.useState(updated);
  useEffect(() => {
    setNewState(LocationsList);
  }, [pState]);
  return (
    <div className="fav-container">
      <div className="fav-wrapper">
        {LocationsList.length > 0 ? (
          <ul className="fav-items">
            {LocationsList.map((local) => {
              <li key={local.locationKey}>
                {local.locationKey}
                {local.name}
                {local.temperature}
                {local.weatherDesc}
              </li>;
            })}
          </ul>
        ) : (
          <h1 className="fav-empty">No Favorite Locations.</h1>
        )}
      </div>
    </div>
  );
};
