import React, { useEffect } from "react";
import "./WeatherCard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { currWeather, foreCast } from "../WeatherAPI/WeatherConfig";
const WeatherCard = (location) => {
  const [weatherMetric, setMetric] = React.useState(null);
  const [weatherText, setWeatherText] = React.useState(null);
  const getCurrentWeather = async (e) => {
    try {
      const response = await fetch(currWeather(location.Key), {
        mode: "cors",
      });
      const data = await response.json();
      setMetric(data.Temperature.Value + data.Temperature.Unit);
      setWeatherText(data.WeatherText);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    async function fetchInitialData() {
      try {
        const response = await fetch(currWeather("215854"), {
          mode: "cors",
        });
        const data = await response.json();
        console.log(data);
        if (data) {
          setMetric(
            data.Temperature.Metric.Value + data.Temperature.Metric.Unit
          );
          setWeatherText(data.WeatherText);
        }
      } catch (err) {
        console.log(err);
      }
    }
    fetchInitialData();
  }, []);
  return (
    <div className="wrapper">
      <div className="content">
        <div className="weather-card">
          <div className="favorites-container">
            <i id="favIcon">
              <FontAwesomeIcon icon={faHeart} />
            </i>
            <button>Add To Favorite</button>
          </div>
          <div className="quick-info">
            <img src=""></img>
            <h3 id="cityName">{location.LocalizedName}</h3>
            <h6 id="currTemp">{weatherMetric}</h6>
          </div>
        </div>
        <div className="weather-description">
          <h1 id="weather-desc">{weatherText}</h1>
        </div>
        <div className="day-cards">
          <ul>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
            <li>
              <img src=""></img>
              <h4>Day</h4>
              <h6>35C</h6>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};
export default WeatherCard;
