import React, { useEffect } from "react";
import "./App.css";
import NavBar from "./NavBar/NavBar";
import Weather from "./Weather/Weather";
import { Favorites } from "./Favorites/Favorites";
import { faHeart, faSun, faCloud } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import Sun from "./Assets/sun.png";
const App = () => {
  const [currPage, setPage] = React.useState(0);
  const [clicked, setClicked] = React.useState();
  useEffect(
    (prevState) => {
      console.log("prevState:", prevState, "==>", clicked);
      if (prevState != clicked) {
        setPage(0);
      }
    },
    [clicked]
  );

  return (
    // <>
    //   <section className="navigation">
    //     <NavBar key={clicked} page={setPage} />
    //   </section>
    //   <section className="main">
    //     {currPage == 0 ? (
    //       <Weather key={clicked} locationKey={clicked} />
    //     ) : (
    //       <Favorites key={clicked} isClicked={setClicked} />
    //     )}
    //   </section>
    // </>
    <figure class="snip1578">
      <img src={Sun} alt="profile-sample4" />
      <figcaption>
        <h3 id="cityName">Tel Aviv</h3>
        <h6 id="currTemp">
          <FontAwesomeIcon className="tempIcon" icon={faSun} />
          31C
        </h6>
      </figcaption>
    </figure>
  );
};
export default App;
