import React, { useEffect } from "react";
import "./WeatherCard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { currWeather, foreCast } from "../WeatherAPI/WeatherConfig";
const WeatherCard = (location) => {
  const [weatherMetric, setMetric] = React.useState(null);
  const [weatherText, setWeatherText] = React.useState(null);
  const [cardData, setCard] = React.useState([]);
  const [loader, setLoader] = React.useState(true);
  const [weatherData, setData] = React.useState(null);
  const getCurrentWeather = async () => {
    try {
      const response = await fetch(currWeather("215854"), {
        mode: "cors",
      });
      const foreCastresponse = await fetch(foreCast("215854"), {
        mode: "cors",
      });
      const data = await response.json();
      const foreCastdata = await foreCastresponse.json();
      setData(data);
      setMetric(
        data[0].Temperature.Metric.Value + data[0].Temperature.Metric.Unit
      );
      setWeatherText(data[0].WeatherText);
      setCard(foreCastdata.DailyForecasts);
      if (cardData && data) {
        console.log(wearherData);
        setLoader(false);
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getCurrentWeather();
  }, []);

  return (
    <div className="wrapper">
      <div className="content">
        <div className="weather-card">
          <div className="favorites-container">
            <i id="favIcon">
              <FontAwesomeIcon icon={faHeart} />
            </i>
            <button>Add To Favorite</button>
          </div>
          <div className="quick-info">
            <img src=""></img>
            <h3 id="cityName">{location.location}</h3>
            <h6 id="currTemp">{weatherMetric}</h6>
          </div>
        </div>
        <div className="weather-description">
          <h1 id="weather-desc">{weatherText}</h1>
        </div>
        <div className="day-cards">
          {loader ? (
            <div></div>
          ) : (
            <ul>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[0].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                <h6>
                  {/* {weatherData[0].Temperature.Maximum.Value +
                    weatherData[0].Temperature.Maximum.Unit} */}
                </h6>
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[1].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                {/* <h6>
                  {data[1].Temperature.Maximum.Value +
                    data[1].Temperature.Maximum.Unit}
                </h6> */}
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[2].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                {/* <h6>
                  {data[2].Temperature.Maximum.Value +
                    data[2].Temperature.Maximum.Unit}
                </h6> */}
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[3].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                {/* <h6>
                  {data[3].Temperature.Maximum.Value +
                    data[3].Temperature.Maximum.Unit}
                </h6> */}
              </li>
              <li>
                <img src=""></img>
                <h4>
                  {new Date(cardData[4].Date).toLocaleString("en-us", {
                    weekday: "long",
                  })}
                </h4>
                {/* <h6>
                  {data[4].Temperature.Maximum.Value +
                    data[4].Temperature.Maximum.Unit}
                </h6> */}
              </li>
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};
export default WeatherCard;
