import React, { useEffect } from "react";
import "./WeatherCard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { currWeather, foreCast } from "../WeatherAPI/WeatherConfig";
const WeatherCard = (location) => {
  console.log(location);
  const [weatherMetric, setMetric] = React.useState(null);
  const [weatherText, setWeatherText] = React.useState(null);
  const getCurrentWeather = async () => {
    try {
      const response = await fetch(currWeather("215854"), {
        mode: "cors",
      });
      const data = await response.json();
      setMetric(
        data[0].Temperature.Metric.Value + data[0].Temperature.Metric.Unit
      );
      setWeatherText(data[0].WeatherText);
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    getCurrentWeather();
  }, []);
