import React, { useEffect } from "react";
import "./Favorites.css";
const LocationsList = []; //List of Objects of Locations added to favorite;
var updated = 0;
export function addToFavorites(
  Key,
  locationName,
  locationMetrics,
  weatherDescription
) {
  const newLocation = {
    locationKey: Key,
    name: locationName,
    temperature: locationMetrics,
    weatherDesc: weatherDescription,
  };
  if (!LocationsList.includes(newLocation)) {
    LocationsList.push(newLocation);
  }
}
export function updater() {
  updated += 1;
}
export const Favorites = ({ isClicked }) => {
  const [pState, setNewState] = React.useState(LocationsList);

  useEffect(() => {
    setNewState(LocationsList);
    isClicked(updated);
  }, [pState]);
  return (
    <div className="fav-container">
      <div className="fav-wrapper">
        {LocationsList.length > 0 ? (
          <ul className="fav-items">
            {pState.map((local, index) => {
              return (
                <li key={index}>
                  {local.locationKey}
                  {local.name}
                  {local.temperature}
                  {local.weatherDesc}
                </li>
              );
            })}
          </ul>
        ) : (
          <h1 className="fav-empty">No Favorite Locations.</h1>
        )}
      </div>
    </div>
  );
};
