import React from "react";

const favLocations = []; //List of Objects of Locations added to favorite;
export function addToFavorites({ favLocation, favLocationWeather }) {
  favLocations.push([favLocation, favLocationWeather]);
}
export const Favorites = () => {
  return (
    <div className="fav-container">
      <div className="fav-wrapper">
        <ul className="fav-items">
          {favLocations.map((local) => {
            <a>
              <li>
                <h3>{local[0].LocalizedName}</h3>
                <h6>
                  {local[1].Temperature.Maximum.Value +
                    "\xB0" +
                    local[1].Temperature.Maximum.Unit}
                </h6>
              </li>
            </a>;
          })}
        </ul>
      </div>
    </div>
  );
};
